                                <button wire:click="previousPage('{{ $paginator->getPageName() }}')" dusk="previousPage{{ $paginator->getPageName() == 'page' ? '' : '.' . $paginator->getPageName() }}.after" rel="prev" class="px-2 py-2" aria-label="{{ __('pagination.previous') }}">
                                    <x-icon icon="previous-page" fill="{{config('constants.icons.color.active')}}"/>
